define('OrderWizard.ShippingAccount', [
    'Wizard.Module', 
    'shippingaccount.tpl',
    'underscore',
    'Utils'
], function (
    WizardModule,
    template,
    _,
    Utils
) {
  'use strict';

  return WizardModule.extend({
      template: template,
      events:{
        'click [name="freighttype"]': 'changeFreightType',
        'change #custbody11': 'changeComment'
      },
      changeComment: function changeComment(e){
        var options = this.model.get('options')
        options.custbody11 = e.currentTarget.value
        this.model.set('options', options)
      },
      changeFreightType: function(e){
        var options = this.model.get('options')
        this.freightType = e.currentTarget.value
        options.custbody7 = this.freightType =='custbody7' ? 'T': 'F'
        options.custbody8= this.freightType =='custbody8' ? 'T': 'F'
        if(this.freightType == 'custbody8'){
          options.custbody_kvc_carrier_acct = 'N/A'
        }else{
          options.custbody_kvc_carrier_acct = ''
        }
        this.model.set('options', options)
        this.render();
      },
      initialize: function (options) {
        WizardModule.prototype.initialize.apply(this, _.toArray(arguments));
      },
      validate: function validate(){
        var options = this.model.get('options')
        if(!this.freightType){
          window.scrollTo({ top: 0, behavior: 'smooth' });
          return jQuery.Deferred().reject(Utils.translate('Please select your shipping carrier'));
        }
        if(this.freightType == 'custbody7' && (!options.custbody_kvc_carrier_acct || options.custbody_kvc_carrier_acct == '')) {
          window.scrollTo({ top: 0, behavior: 'smooth' });
          return jQuery.Deferred().reject(Utils.translate('Please enter account number'));
        }
        return jQuery.Deferred().resolve();
      },
      submit: function () {
          return this.validate();
      },
      getContext: function getContext()
      {
        return {
          custbody_kvc_carrier_acct: this.model.get('options').custbody_kvc_carrier_acct,
          isAccReadOnly: this.freightType == 'custbody8',
          custbody11: this.model.get('options').custbody11,
          custbody7: this.model.get('options').custbody7 == 'T',
          custbody8: this.model.get('options').custbody8 == 'T'
        };
      }
    });
});

define('ShippingAccount.Main', [
	'OrderWizard.ShippingAccount'
], function (
	OrderWizardShippingAccount
) {
	'use strict';

	return  {
		mountToApp: function mountToApp (container)
		{
			var checkout = container.getComponent('Checkout');
			checkout.addModuleToStep({
				step_url: 'shipping/address' ,
				module: {
					id: 'shipacc',
					index: 13,
					classname: 'OrderWizard.ShippingAccount'
				}
			});
		}
	};
});

